import React, {Component} from 'react';

class App extends Component {

	state = {
	        tarefas: []
	      }


		  componentDidMount() {
		          fetch('http://localhost:4000/tarefas')
		          .then(res => res.json())
		          .then((data) => {
		            this.setState({ tarefas: data })
		          })
		          .catch(console.log)
		        }

  render () {
    return (
		<div class="card">
            <div class="card-body">
              <h5 class="card-title">Steve Jobs</h5>
              <h6 class="card-subtitle mb-2 text-muted">steve@apple.com</h6>
              <p class="card-text">Stay Hungry, Stay Foolish</p>
            </div>
          </div>
    );
  }
}

export default App;
