import React, { Component } from 'react'
import axios from 'axios'
import update from 'immutability-helper'

class TodosContainer extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: []
    }
  }

  getTodos() {
    axios.get('/tarefas')
    .then(response => {
      this.setState({todos: response.data})
    })
    .catch(error => console.log(error))
  }


  createTodo = (e) => {
	var tarefa = document.getElementById("tarefa").value;
	var descricao = document.getElementById("descricao").value;

	console.log(tarefa+' - '+descricao)

    axios.post('/nova-tarefa', {todo: {tarefa: tarefa, descricao: descricao}})
    .then(response => {
      const todos = update(this.state.todos, {
        $splice: [[0, 0, response.data]]
      })
      this.setState({
        todos: todos
      })
    })
    .catch(error => console.log(error))   
  }


  componentDidMount() {
    this.getTodos()
  }

  render() {
    return (
      <div>
        <div className="inputContainer">
          <input className="taskInput" type="text" id="tarefa" 
            placeholder="Adicionar tarefa" maxLength="50" />
          <input className="taskInput" type="text" id="descricao" 
            placeholder="Descrição da tarefa" maxLength="500" />
		  <button variant="primary" onClick={this.createTodo} >
		    Gravar tarefa
		  </button>
        </div>  	    
	<div className="listWrapper">
	   <ul className="taskList">
		  {this.state.todos.map((todo) => {
		    return(
		      <li className="task" todo={todo} key={todo.id}>
			<input className="taskCheckbox" type="checkbox" />              
			<label className="taskLabel">{todo.tarefa}</label>
			<span className="deleteTaskBtn">x</span>
		      </li>
		    )       
		  })} 	    
	   </ul>
	</div>
     </div>
    )
  }
}

export default TodosContainer
